import { useState } from "react";

const App = () => {
  const [items, setItems] = useState([]);
  const [selectedItem, setSelectedItem] = useState("");
  const [weight, setWeight] = useState(0);
  const [buyingPrice, setBuyingPrice] = useState(0);
  const [sellingPrice, setSellingPrice] = useState(0);
  const [totalCost, setTotalCost] = useState(0);
  const [totalPrice, setTotalPrice] = useState(0);
  const [profit, setProfit] = useState(0);

  const handleItemChange = (e) => {
    setSelectedItem(e.target.value);
  };

  const handleWeightChange = (e) => {
    setWeight(e.target.value);
  };

  const handleBuyingPriceChange = (e) => {
    setBuyingPrice(e.target.value);
  };

  const handleSellingPriceChange = (e) => {
    setSellingPrice(e.target.value);
  };

  const handleAddItem = (e) => {
    e.preventDefault();
    const newItem = {
      name: selectedItem,
      buyingPrice: buyingPrice,
      sellingPrice: sellingPrice,
    };
    setItems([...items, newItem]);
    setSelectedItem("");
    setBuyingPrice(0);
    setSellingPrice(0);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const selected = items.find((item) => item.name === selectedItem);
    const cost = selected.buyingPrice * weight;
    const price = selected.sellingPrice * weight;
    const profit = price - cost;
    setTotalCost(cost);
    setTotalPrice(price);
    setProfit(profit);
  };

  return (
    <div>
      <div>
        <h2>Set prices for the day:</h2>
        <form onSubmit={handleAddItem}>
          <label htmlFor="item">Item:</label>
          <select id="item" value={selectedItem} onChange={handleItemChange}>
            <option value="">Select an item</option>
            <option value="apple">Apple</option>
            <option value="carrot">Carrot</option>
            {/* add more items here */}
          </select>
          <label htmlFor="buying-price">Buying Price:</label>
          <input
            type="number"
            id="buying-price"
            value={buyingPrice}
            onChange={handleBuyingPriceChange}
          />
          <label htmlFor="selling-price">Selling Price:</label>
          <input
            type="number"
            id="selling-price"
            value={sellingPrice}
            onChange={handleSellingPriceChange}
          />
          <button type="submit">Add item</button>
        </form>
      </div>
      <div>
        <h2>Calculate total cost, price and profit:</h2>
        <form onSubmit={handleSubmit}>
          <label htmlFor="item-select">Select Item:</label>
          <select
            id="item-select"
            value={selectedItem}
            onChange={handleItemChange}
          >
            <option value="">Select an item</option>
            {items.map((item) => (
              <option key={item.name} value={item.name}>
                {item.name}
              </option>
            ))}
          </select>
          <label htmlFor="weight">Weight (kg):</label>
          <input
            type="number"
            id="weight"
            value={weight}
            onChange={handleWeightChange}
          />
          <button type="submit">Calculate</button>
        </form>
        <div>
          <h3>Total cost: {totalCost}</h3>
          <h3>Total price: {totalPrice}</h3>
          <h3>Profit: {profit}</h3>
        </div>
      </div>
    </div>
  );
};

export default App;
