import React, { useState } from "react";
import "./index.css";

const App = () => {
  const [before, after] = useState();

  const inputEvent = (event) => {
    after(event.target.value);
  };

  return (
    <>
      <div>
        <h1>You Are : {before} Years Old</h1>
        <input
          type="number"
          pattern="[0-9]*"
          max={12}
          placeholder="Enter Your Age"
          onChange={inputEvent}
          value={before}
        />
        <button onClick={inputEvent}>Find Age</button>
      </div>
    </>
  );
};

export default App;
