import React, { useState } from "react";
import "./index.css";

const App = () => {
  let time = new Date().toLocaleTimeString();

  const Function = () => {
    const [time, uTime] = useState(time);
  };

  return (
    <>
      <div className="container">
        <h1 className="heading">{time}</h1>
        <button onClick={Function} className="btn">
          Get Time
        </button>
      </div>
    </>
  );
};

export default App;
