import React, { useState } from "react";
import "./index.css";

const App = () => {
  const black = "#000000";
  const [bg, setbg] = useState(black);
  const [btn, setbtn] = useState("Dont click please");

  const normal = () => {
    let red = "#FF0000";
    setbg(red);
    setbtn("o baaz aja banda da puttar ban ðŸ–ï¸ðŸ–ï¸ðŸ–ï¸");
    vid = "https://youtu.be/c2KK4nSiOO8";
  };

  const colour = () => {
    let newbg = "#000000";
    setbg(newbg);
    setbtn("Dont Double Click ðŸ˜ ðŸ˜ ðŸ˜ ");
  };
  return (
    <>
      <video src={vid} />
      <div className="container" style={{ background: bg }}>
        <button onClick={colour} onDoubleClick={normal}>
          {btn}
        </button>
      </div>
    </>
  );
};

export default App;
