import React, { useState } from "react";

const App = () => {
  const [items, setItems] = useState([
    { name: "Apple", buyingPrice: 100, sellingPrice: 150 },
    { name: "Carrot", buyingPrice: 50, sellingPrice: 75 },
  ]);
  const [cart, setCart] = useState([]);
  const [selectedItem, setSelectedItem] = useState(items[0]);

  const handleItemAddToCart = (weight) => {
    const newCartItem = {
      name: selectedItem.name,
      weight: weight,
      profit:
        weight * selectedItem.sellingPrice - weight * selectedItem.buyingPrice,
      totalCost: weight * selectedItem.sellingPrice,
    };
    setCart([...cart, newCartItem]);
  };

  return (
    <div>
      <h1>Shop</h1>
      <div>
        <label>
          Item:
          <select
            value={selectedItem.name}
            onChange={(e) =>
              setSelectedItem(
                items.find((item) => item.name === e.target.value)
              )
            }
          >
            {items.map((item) => (
              <option key={item.name} value={item.name}>
                {item.name}
              </option>
            ))}
          </select>
        </label>
        <br />
        <label>
          Weight (kg):
          <input
            type="number"
            defaultValue={1}
            min={0}
            step={0.1}
            ref={(input) => (selectedItem.weightInput = input)}
          />
        </label>
        <button
          onClick={() => handleItemAddToCart(selectedItem.weightInput.value)}
        >
          Add to cart
        </button>
      </div>
      <div>
        <h2>Cart</h2>
        {cart.map((cartItem, index) => (
          <div key={index}>
            <p>Name: {cartItem.name}</p>
            <p>Weight: {cartItem.weight}</p>
            {index === cart.length - 1 && (
              <div>
                <p>
                  Total Profit: {cart.reduce((acc, cur) => acc + cur.profit, 0)}
                </p>
                <p>
                  Total Cost:{" "}
                  {cart.reduce((acc, cur) => acc + cur.totalCost, 0)}
                </p>
              </div>
            )}
            <hr />
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
