import React, { useState } from "react";

const App = () => {
  const [items, setItems] = useState([]);
  const [selectedItem, setSelectedItem] = useState("");
  const [weight, setWeight] = useState(0);
  const [buyingPrice, setBuyingPrice] = useState("");
  const [sellingPrice, setSellingPrice] = useState("");

  const handleItemChange = (event) => {
    setSelectedItem(event.target.value);
  };

  const handleWeightChange = (event) => {
    setWeight(event.target.value);
  };

  const handleBuyingPriceChange = (event) => {
    const itemName = event.target.value;
    const existingItem = items.find((item) => item.name === itemName);
    if (existingItem) {
      const updatedItem = { ...existingItem, buyingPrice: event.target.value };
      const updatedItems = items.map((item) =>
        item.name === itemName ? updatedItem : item
      );
      setItems(updatedItems);
    } else {
      const newItem = { name: itemName, buyingPrice: event.target.value };
      setItems([...items, newItem]);
    }
  };

  const handleSellingPriceChange = (event) => {
    const itemName = event.target.value;
    const existingItem = items.find((item) => item.name === itemName);
    if (existingItem) {
      const updatedItem = { ...existingItem, sellingPrice: event.target.value };
      const updatedItems = items.map((item) =>
        item.name === itemName ? updatedItem : item
      );
      setItems(updatedItems);
    } else {
      const newItem = { name: itemName, sellingPrice: event.target.value };
      setItems([...items, newItem]);
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const selectedItemObj = items.find((item) => item.name === selectedItem);
    const totalCost = selectedItemObj.buyingPrice * weight;
    const totalPrice = selectedItemObj.sellingPrice * weight;
    const profit = totalPrice - totalCost;
    alert(
      `Total Cost: ${totalCost}, Total Price: ${totalPrice}, Profit: ${profit}`
    );
  };

  return (
    <div>
      <div>
        <h3>Select item and weight:</h3>
        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor="item">Select Item:</label>
            <select id="item" value={selectedItem} onChange={handleItemChange}>
              <option value="">Select Item</option>
              {items.map((item) => (
                <option key={item.name} value={item.name}>
                  {item.name}
                </option>
              ))}
            </select>
          </div>
          <div>
            <label htmlFor="weight">Weight (in kg):</label>
            <input
              id="weight"
              type="number"
              value={weight}
              onChange={handleWeightChange}
            />
          </div>
          <button type="submit">Calculate</button>
        </form>
      </div>
      <div>
        <h3>Set buying and selling price for the day:</h3>
        <form>
          <div>
            <label htmlFor="buyingPriceItem">Select Item:</label>
            <select id="buyingPriceItem" onChange={handleBuyingPriceChange}>
              <option value="">Select Item</option>
              {items.map((item) => (
                <option key={item.name} value={item.name}>
                  {item.name}
                </option>
              ))}
            </select>
            <input
              type="number"
              onChange={handleBuyingPriceChange}
              placeholder="Buying Price (per kg)"
            />
          </div>
          <div>
            <label htmlFor="sellingPriceItem">Select Item:</label>
            <select id="sellingPriceItem" onChange={handleSellingPriceChange}>
              <option value="">Select Item</option>
              {items.map((item) => (
                <option key={item.name} value={item.name}>
                  {item.name}
                </option>
              ))}
            </select>
            <input
              type="number"
              onChange={handleSellingPriceChange}
              placeholder="Selling Price (per kg)"
            />
          </div>
        </form>
      </div>{" "}
      <div>
        <h3>Calculate total cost, price and profit:</h3>
        <div>
          <label htmlFor="itemForCalculation">Select Item:</label>
          <select id="itemForCalculation">
            <option value="">Select Item</option>
            {items.map((item) => (
              <option key={item.name} value={item.name}>
                {item.name}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label htmlFor="totalWeight">Total Weight (in kg):</label>
          <input id="totalWeight" type="number" />
        </div>
        <div>
          <button>Calculate</button>
        </div>
        <div>
          <p>Total Cost: </p>
          <p>Total Price: </p>
          <p>Profit: </p>
        </div>
      </div>
    </div>
  );
};

export default App;
