import React, { useState } from "react";
import "./index.css";

const ITEMS = {
  apple: { name: "Apple", price: 10 },
  banana: { name: "Banana", price: 20 },
  carrot: { name: "Carrot", price: 30 },
  tomato: { name: "Tomato", price: 40 },
};

function App() {
  const [itemList, setItemList] = useState([]);
  const [item, setItem] = useState({
    name: "",
    weight: "",
  });

  function handleChange(event) {
    const { name, value } = event.target;
    setItem((prevItem) => ({ ...prevItem, [name]: value }));
  }

  function addItem() {
    const newItem = {
      name: ITEMS[item.name].name,
      price: ITEMS[item.name].price,
      weight: item.weight,
      total: ITEMS[item.name].price * item.weight,
    };
    setItemList((prevItems) => [...prevItems, newItem]);
    setItem({ name: "", weight: "" });
    alert("Item added!");
  }

  return (
    <div>
      <h1>Welcome to My Shop</h1>
      <form>
        <label htmlFor="item-select">Select an item:</label>
        <select
          id="item-select"
          name="name"
          value={item.name}
          onChange={handleChange}
        >
          <option value="">Select an item</option>
          <option value="apple">Apple</option>
          <option value="banana">Banana</option>
          <option value="carrot">Carrot</option>
          <option value="tomato">Tomato</option>
        </select>
        <br />
        <label htmlFor="item-weight">Weight in KG:</label>
        <input
          type="number"
          id="item-weight"
          name="weight"
          value={item.weight}
          onChange={handleChange}
        />
        <br />
        <button type="button" onClick={addItem}>
          Add Item
        </button>
      </form>
      {itemList.map((item, index) => (
        <p key={index}>
          {item.name} - {item.total.toFixed(2)} PKR
        </p>
      ))}
      <button>Add Item and Price</button>
    </div>
  );
}

export default App;
