import React, { useState } from "react";
import "./index.css";

function App() {
  const [itemList, setItemList] = useState([]);
  const [item, setItem] = useState({
    name: "",
    price: "",
    cost: "",
    weight: "",
  });
  const [customItem, setCustomItem] = useState({
    name: "",
    price: "",
  });

  const itemPrices = {
    apple: 100,
    banana: 200,
    carrot: 80,
  };

  function handleChange(event) {
    const { name, value } = event.target;
    setItem((prevItem) => ({ ...prevItem, [name]: value }));
  }

  function addCustomItem() {
    const newItem = {
      name: customItem.name,
      price: customItem.price * item.weight,
      cost: customItem.cost * item.weight,
      weight: item.weight,
    };
    setItemList((prevItems) => [...prevItems, newItem]);
    setCustomItem({ name: "", price: "", cost: "" });
    setItem({ name: "", price: "", cost: "", weight: "" });
  }

  function addItem() {
    const newItem = {
      name: item.name,
      price: itemPrices[item.name] * item.weight,
      cost: item.cost,
      weight: item.weight,
    };
    setItemList((prevItems) => [...prevItems, newItem]);
    setItem({ name: "", price: "", cost: "", weight: "" });
  }

  const totalPrice = itemList.reduce((sum, item) => sum + item.price, 0);
  const totalCost = itemList.reduce((sum, item) => sum + item.cost, 0);

  return (
    <div>
      <h1>SHAQEEL STORE</h1>
      <form>
        <label htmlFor="item-select">Select an item:</label>
        <select
          id="item-select"
          name="name"
          value={item.name}
          onChange={handleChange}
        >
          <option value="">Select an item</option>
          <option value="apple">Apple</option>
          <option value="banana">Banana</option>
          <option value="carrot">Carrot</option>
        </select>
        {item.name && (
          <div>
            <label>Price per KG (PKR): {itemPrices[item.name]}</label>
          </div>
        )}
        <br />
        <label htmlFor="item-cost">Cost per KG (PKR):</label>
        <input
          type="number"
          id="item-cost"
          name="cost"
          value={item.cost}
          onChange={handleChange}
        />
        <br />
        <label htmlFor="item-price">Selling Price per KG (PKR):</label>
        <input
          type="number"
          id="item-price"
          name="price"
          value={item.price}
          onChange={handleChange}
        />
        <br />
        <label htmlFor="item-weight">Weight in KG:</label>
        <input
          type="number"
          id="item-weight"
          name="weight"
          value={item.weight}
          onChange={handleChange}
        />

        <button type="button" onClick={addItem}>
          Add Item
        </button>
        <br />
      </form>
      <br />
      <h3>Custom Items:</h3>
      <form>
        <label htmlFor="custom-item-name">Item Name:</label>
        <input
          type="text"
          id="custom-item-name"
          value={customItem.name}
          onChange={(e) =>
            setCustomItem({ ...customItem, name: e.target.value })
          }
        />
        <br />
        <label htmlFor="custom-item-price">Selling Price per KG (PKR):</label>
        <input
          type="number"
          id="custom-item-price"
          value={customItem.price}
          onChange={(e) =>
            setCustomItem({ ...customItem, price: e.target.value })
          }
        />
        <br />
        <label htmlFor="custom-item-cost">Cost per KG (PKR):</label>
        <input
          type="number"
          id="custom-item-cost"
          value={customItem.cost}
          onChange={(e) =>
            setCustomItem({ ...customItem, cost: e.target.value })
          }
        />
        <br />
        <label htmlFor="custom-item-weight">Weight in KG:</label>
        <input
          type="number"
          id="custom-item-weight"
          value={item.weight}
          onChange={(e) => setItem({ ...item, weight: e.target.value })}
        />{" "}
        <button type="button" onClick={addCustomItem}>
          Add Custom Item
        </button>
      </form>
      <br />
      <h3>Cart:</h3>
      <ul>
        {itemList.map((item, index) => (
          <li key={index}>
            {item.name} - {item.weight}KG - {item.price}PKR
          </li>
        ))}
      </ul>
      <h3>Total Price: {totalPrice}PKR</h3>
      <h3>Total Cost: {totalCost}PKR</h3>
      <h3>Profit: {totalPrice - totalCost}PKR</h3>
    </div>
  );
}
