import React, { useState } from "react";

const App = () => {
  const [selectedItem, setSelectedItem] = useState("");
  const [weight, setWeight] = useState("");
  const [buyingPricePerKg, setBuyingPricePerKg] = useState("");
  const [sellingPricePerKg, setSellingPricePerKg] = useState("");

  const [items, setItems] = useState([
    { name: "Apple", buyingPrice: "", sellingPrice: "" },
    { name: "Carrot", buyingPrice: "", sellingPrice: "" },
  ]);

  const handleBuyingPriceChange = (event, index) => {
    const newItems = [...items];
    newItems[index].buyingPrice = event.target.value;
    setItems(newItems);
  };

  const handleSellingPriceChange = (event, index) => {
    const newItems = [...items];
    newItems[index].sellingPrice = event.target.value;
    setItems(newItems);
  };

  const handleSelectedItemChange = (event) => {
    setSelectedItem(event.target.value);
  };

  const handleWeightChange = (event) => {
    setWeight(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const selectedItemIndex = items.findIndex(
      (item) => item.name === selectedItem
    );
    const totalPrice = weight * items[selectedItemIndex].sellingPrice;
    const totalCost = weight * items[selectedItemIndex].buyingPrice;
    const totalProfit = totalPrice - totalCost;
    console.log(
      `Selected Item: ${selectedItem}\nWeight: ${weight}\nTotal Cost: ${totalCost}\nTotal Price: ${totalPrice}\nTotal Profit: ${totalProfit}`
    );
  };

  const handleAddItem = () => {
    const newItem = { name: "", buyingPrice: "", sellingPrice: "" };
    setItems([...items, newItem]);
  };

  return (
    <div>
      <div>
        <h2>Price for the day</h2>
        {items.map((item, index) => (
          <div key={index}>
            <label htmlFor={`buying-price-${index}`}>
              {item.name} buying price:
            </label>
            <input
              id={`buying-price-${index}`}
              type="number"
              value={item.buyingPrice}
              onChange={(event) => handleBuyingPriceChange(event, index)}
            />
            <label htmlFor={`selling-price-${index}`}>
              {item.name} selling price:
            </label>
            <input
              id={`selling-price-${index}`}
              type="number"
              value={item.sellingPrice}
              onChange={(event) => handleSellingPriceChange(event, index)}
            />
          </div>
        ))}
        <button onClick={handleAddItem}>Add Item</button>
      </div>

      <div>
        <h2>Select item and enter weight</h2>
        <form onSubmit={handleSubmit}>
          <label htmlFor="item-select">Select item:</label>
          <select
            id="item-select"
            value={selectedItem}
            onChange={handleSelectedItemChange}
          >
            <option value="">Select an item</option>
            {items.map((item, index) => (
              <option key={index} value={item.name}>
                {item.name}
              </option>
            ))}
          </select>
          <br />
          <label htmlFor="weight-input">Weight (kg):</label>
          <input
            id="weight-input"
            type="number"
            value={weight}
            onChange={handleWeightChange}
          />
          <br />
          <button type="submit">Calculate</button>
        </form>
      </div>
    </div>
  );
};

export default App;
