import React, { useState } from "react";
import "./index.css";

const App = () => {
  const [fullName, setFullName] = useState({
    fName: "",
    lName: "",
    eMail: "",
    pNumber: "",
  });

  const onSubmit = (event) => {
    event.preventDefault();
    alert("form sumitted");
  };

  const inputEvent = (event) => {
    console.log(event.target.value);
    console.log(event.target.name);

    const { value, name } = event.target;

    setFullName((preValue) => {
      if (name === "fName") {
        return { fName: value, lName: preValue.lName };
      } else if (name === "lName") {
        return {
          fName: preValue.fName,
          lName: value,
          eMail: preValue.eMail,
          pNumber: preValue.pNumber,
        };
      } else if (name === "eMail") {
        return {
          fName: preValue.fName,
          lName: preValue.lName,
          eMail: value,
          pNumber: preValue.pNumber,
        };
      } else if (name === "pNumber") {
        return {
          fName: preValue.fName,
          lName: preValue.lName,
          eMail: preValue.eMail,
          pNumber: value,
        };
      }
    });
  };

  return (
    <>
      <div class="container">
        <div class="form-container">
          <form onSubmit={onSubmit}>
            <div>
              <h1>Hello</h1>
              <input
                placeholder="Enter Your First Name"
                name="fName"
                onChange={inputEvent}
                value={fullName.fName}
              />
              <br />
              <input
                placeholder="Enter Your Last Name"
                name="lName"
                onChange={inputEvent}
                value={fullName.lName}
              />
              <br />
              <input
                placeholder="Enter Your Email"
                name="eMail"
                onChange={inputEvent}
                value={fullName.eMail}
              />
              <br />
              <input
                placeholder="Enter Your Phone Number"
                name="pNumber"
                onChange={inputEvent}
                value={fullName.pNumber}
              />
              <button type="submit">Submit</button>
            </div>
          </form>
        </div>
        <div class="info-container">
          <div class="info-box">
            <h2>Entered Information:</h2>
            <p>First Name: {fullName.fName}</p>
            <p>Last Name: {fullName.lName}</p>
            <p>Email: {fullName.eMail}</p>
            <p>Phone Number: {fullName.pNumber}</p>
          </div>
        </div>
      </div>
    </>
  );
};

export default App;
