import React, { useState } from "react";
import "./index.css";

const App = () => {
  const black = "#000000";
  const [bg, setbg] = useState(black);
  const [btn, setbtn] = useState("Dont leave me alone");
  src = "https://youtu.be/c2KK4nSiOO8";

  const normal = () => {
    setbtn("Why did you leave me ðŸ˜¢ðŸ˜¢ðŸ˜¢ðŸ˜¢");
  };

  const colour = () => {
    let newbg = "#000000";
    setbg(newbg);
    setbtn("Dont leave me alone");
  };
  return (
    <>
      <video controls width="100%">
        <source src={{ src }} type="" />
        Sorry, your browser doesn't support embedded videos.
      </video>
      <div className="container" style={{ background: bg }}>
        <button onMouseEnter={colour} onMouseLeave={normal}>
          {btn}
        </button>
      </div>
    </>
  );
};

export default App;
