import React, { useState } from "react";
import "./index.css";

const App = () => {
  const [items, setItems] = useState([
    { name: "Apple", buyingPrice: 100, sellingPrice: 150 },
    { name: "Carrot", buyingPrice: 50, sellingPrice: 75 },
  ]);
  const [cart, setCart] = useState([]);
  const [selectedItem, setSelectedItem] = useState(items[0]);
  const [orderCompleted, setOrderCompleted] = useState(false);
  const [orderCount, setOrderCount] = useState(0);

  const handleItemAddToCart = (weight) => {
    const existingCartItem = cart.find(
      (item) => item.name === selectedItem.name
    );
    if (existingCartItem) {
      const updatedCart = cart.map((item) => {
        if (item.name === selectedItem.name) {
          return {
            ...item,
            weight: parseFloat(item.weight) + parseFloat(weight),
            totalCost:
              parseFloat(item.totalCost) +
              parseFloat(weight * selectedItem.sellingPrice),
            profit:
              (parseFloat(item.weight) + parseFloat(weight)) *
                selectedItem.sellingPrice -
              (parseFloat(item.weight) + parseFloat(weight)) *
                selectedItem.buyingPrice,
          };
        } else {
          return item;
        }
      });
      setCart(updatedCart);
    } else {
      const newCartItem = {
        name: selectedItem.name,
        weight: weight,
        profit:
          weight * selectedItem.sellingPrice -
          weight * selectedItem.buyingPrice,
        totalCost: weight * selectedItem.sellingPrice,
      };
      setCart([...cart, newCartItem]);
    }
    setOrderCompleted(true);
    setOrderCount(orderCount + 1);
  };

  const handleNextOrder = () => {
    setCart([]);
    setOrderCompleted(false);
  };

  const totalProfit = cart.reduce((acc, cur) => acc + cur.profit, 0);
  const totalCost = cart.reduce((acc, cur) => acc + cur.totalCost, 0);
  const numberOfOrders = orderCount;
  const totalSales = totalCost + totalProfit;

  return (
    <div>
      <h1>Shop</h1>
      <div>
        <label>
          Item:
          <select
            value={selectedItem.name}
            onChange={(e) =>
              setSelectedItem(
                items.find((item) => item.name === e.target.value)
              )
            }
          >
            {items.map((item) => (
              <option key={item.name} value={item.name}>
                {item.name}
              </option>
            ))}
          </select>
        </label>
        <br />
        <label>
          Weight (kg):
          <input
            type="number"
            defaultValue={1}
            min={0}
            step={0.1}
            onChange={(e) => {
              const weight = e.target.value;
              handleItemAddToCart(weight);
            }}
          />
        </label>
        <br />
        <button onClick={handleNextOrder}>Next Order</button>
      </div>
      {orderCompleted && (
        <div>
          <h2>Order {numberOfOrders}</h2>
          <table>
            <thead>
              <tr>
                <th>Item</th>
                <th>Weight (kg)</th>
                <th>Total Cost</th>
                <th>Total Revenue</th>
                <th>Profit</th>
              </tr>
            </thead>
            <tbody>
              {cart.map((item, index) => (
                <tr key={index}>
                  <td>{item.name}</td>
                  <td>{item.weight.toFixed(2)}</td>
                  <td>${item.totalCost.toFixed(2)}</td>
                  <td>
                    ${(item.weight * selectedItem.sellingPrice).toFixed(2)}
                  </td>
                  <td>${item.profit.toFixed(2)}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <div>
            <p>Total Sales: ${totalSales.toFixed(2)}</p>
            <p>Total Profit: ${totalProfit.toFixed(2)}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
