import React, { useState } from "react";

const App = () => {
  const [items, setItems] = useState([
    { name: "Apple", buyingPrice: 100, sellingPrice: 150 },
    { name: "Carrot", buyingPrice: 50, sellingPrice: 75 },
  ]);
  const [cart, setCart] = useState([]);

  const handleItemAddToCart = (item, weight) => {
    const newCartItem = {
      name: item.name,
      weight: weight,
      profit: weight * item.sellingPrice - weight * item.buyingPrice,
      totalCost: weight * item.sellingPrice,
    };
    setCart([...cart, newCartItem]);
  };

  return (
    <div>
      <h1>Shop</h1>
      <div>
        {items.map((item) => (
          <div key={item.name}>
            <h2>{item.name}</h2>
            <p>Buying price per kg: {item.buyingPrice}</p>
            <p>Selling price per kg: {item.sellingPrice}</p>
            <label>
              Weight (kg):
              <input
                type="number"
                defaultValue={1}
                min={0}
                step={0.1}
                ref={(input) => (item.weightInput = input)}
              />
            </label>
            <button
              onClick={() => handleItemAddToCart(item, item.weightInput.value)}
            >
              Add to cart
            </button>
          </div>
        ))}
      </div>
      <div>
        <h2>Cart</h2>
        {cart.map((cartItem, index) => (
          <div key={index}>
            <p>Name: {cartItem.name}</p>
            <p>Weight: {cartItem.weight}</p>
            <p>Profit: {cartItem.profit}</p>
            <p>Total cost: {cartItem.totalCost}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
