import React, { useState } from "react";

export default function FaheemDancer(props) {
  const [Text, setText] = useState("kaka nashe ich");

  const handleUpClick = () => {
    console.log("The button was clicked kakakakak");
    setText("click kyu kia tooonenenenenen");
  };

  const handleOnChange = () => {
    console.log("change hoagaya bhai shaab");
    setText(event);
  };

  return (
    <div>
      <h1>{props.head}</h1>
      <div className="mb-3">
        <textarea
          value={Text}
          onChange={handleOnChange}
          className="form-control"
          id="exampleFormControlTextarea1"
          rows="10"
        ></textarea>
      </div>
      <button className="btn btn-primary" onClick={handleUpClick}>
        Convert To Uppercase
      </button>
    </div>
  );
}
