import React, { useState, useEffect } from "react";
import "./index.css";

const App = () => {
  const [items, setItems] = useState([
    { name: "Apple", buyingPrice: 100, sellingPrice: 150 },
    { name: "Carrot", buyingPrice: 50, sellingPrice: 75 },
  ]);
  const [cart, setCart] = useState([]);
  const [selectedItem, setSelectedItem] = useState(items[0]);
  const [orderCompleted, setOrderCompleted] = useState(false);
  const [weight, setWeight] = useState("");
  const [newItemName, setNewItemName] = useState("");
  const [newItemBuyingPrice, setNewItemBuyingPrice] = useState("");
  const [newItemSellingPrice, setNewItemSellingPrice] = useState("");
  const [totalOrders, setTotalOrders] = useState(0);
  const [netProfit, setNetProfit] = useState(0);
  const [totalCost, setTotalCost] = useState(0);
  const [numberOfOrders, setNumberOfOrders] = useState(0);

  const handleItemAddToCart = (weight) => {
    const existingCartItem = cart.find(
      (item) => item.name === selectedItem.name
    );

    if (existingCartItem) {
      const updatedCart = cart.map((item) => {
        if (item.name === selectedItem.name) {
          return {
            ...item,
            weight: parseFloat(item.weight) + parseFloat(weight),
            totalCost:
              parseFloat(item.totalCost) +
              parseFloat(weight * selectedItem.sellingPrice),
            profit:
              (parseFloat(item.weight) + parseFloat(weight)) *
                selectedItem.sellingPrice -
              (parseFloat(item.weight) + parseFloat(weight)) *
                selectedItem.buyingPrice,
          };
        } else {
          return item;
        }
      });
      setCart(updatedCart);
    } else {
      const newCartItem = {
        name: selectedItem.name,
        weight: weight,
        profit:
          weight * selectedItem.sellingPrice -
          weight * selectedItem.buyingPrice,
        totalCost: weight * selectedItem.sellingPrice,
      };
      setCart([...cart, newCartItem]);
    }

    setOrderCompleted(true);
  };

  const handleNextOrder = () => {
    setCart([]);
    setOrderCompleted(false);
    setTotalOrders(totalOrders + 1);
    setNetProfit(0);
    setTotalCost(0);
    setNumberOfOrders(cart.length);
  };

  const handleNewItemSubmit = (event) => {
    event.preventDefault();
    const newItem = {
      name: newItemName,
      buyingPrice: newItemBuyingPrice,
      sellingPrice: newItemSellingPrice,
    };
    setItems([...items, newItem]);
    setNewItemName("");
    setNewItemBuyingPrice("");
    setNewItemSellingPrice("");
  };

  useEffect(() => {
    const newNetProfit = cart.reduce((acc, cur) => acc + cur.profit, 0);
    const newTotalCost = cart.reduce((acc, cur) => acc + cur.totalCost, 0);
    const newNumberOfOrders = cart.length;
    setNetProfit(newNetProfit);
    setTotalCost(newTotalCost);
    setNumberOfOrders(newNumberOfOrders);
  }, [cart]);

  return (
    <div>
      <h1>Shop</h1>
      <div>
        <label>
          Item:
          <select
            value={selectedItem.name}
            onChange={(e) =>
              setSelectedItem(
                items.find((item) => item.name === e.target.value)
              )
            }
          >
            {items.map((item) => (
              <option key={item.name} value={item.name}>
                {item.name}
              </option>
            ))}
          </select>
        </label>
        <label>
          Weight:
          <input type="number" step="0.01" min="0" />
        </label>
        <button onClick={() => handleItemAddToCart(weight)}>Add to Cart</button>
      </div>
      {orderCompleted ? (
        <div>
          <h2>Order Completed!</h2>
          <p>Total Cost: {totalCost}</p>
          <p>Net Profit: {netProfit}</p>
          <button onClick={handleNextOrder}>Next Order</button>
        </div>
      ) : (
        <div>
          <h2>Cart</h2>
          <ul>
            {cart.map((item, index) => (
              <li key={index}>
                {item.name} - {item.weight} kg - Total Cost: {item.totalCost} -{" "}
                Profit: {item.profit}
              </li>
            ))}
          </ul>
        </div>
      )}

      <h2>Add New Item</h2>
      <form onSubmit={handleNewItemSubmit}>
        <label>
          Name:
          <input
            type="text"
            value={newItemName}
            onChange={(e) => setNewItemName(e.target.value)}
          />
        </label>
        <label>
          Buying Price:
          <input
            type="number"
            step="0.01"
            min="0"
            value={newItemBuyingPrice}
            onChange={(e) => setNewItemBuyingPrice(e.target.value)}
          />
        </label>
        <label>
          Selling Price:
          <input
            type="number"
            step="0.01"
            min="0"
            value={newItemSellingPrice}
            onChange={(e) => setNewItemSellingPrice(e.target.value)}
          />
        </label>
        <button type="submit">Add Item</button>
      </form>

      <h2>Statistics</h2>
      <p>Total Orders: {totalOrders}</p>
      <p>Total Cost: {totalCost}</p>
      <p>Net Profit: {netProfit}</p>
      <p>Number of Orders: {numberOfOrders}</p>
    </div>
  );
};

export default App;
