import React, { useState } from "react";
import "./index.css";

const App = () => {
  const [items, setItems] = useState([
    { name: "Apple", buyingPrice: 100, sellingPrice: 150 },
    { name: "Carrot", buyingPrice: 50, sellingPrice: 75 },
  ]);
  const [cart, setCart] = useState([]);
  const [selectedItem, setSelectedItem] = useState({});
  const [weight, setWeight] = useState("");
  const [orderCompleted, setOrderCompleted] = useState(false);
  const [totalOrders, setTotalOrders] = useState(0);
  const [netProfit, setTotalProfit] = useState(0);
  const [totalSales, setTotalSales] = useState(0);

  const handleItemAddToCart = () => {
    if (selectedItem.name === undefined || weight === "") {
      alert("Please select an item and enter the weight");
      return;
    }

    if (cart.some((item) => item.name === selectedItem.name)) {
      const updatedCart = cart.map((item) => {
        if (item.name === selectedItem.name) {
          const newWeight = parseFloat(item.weight) + parseFloat(weight);
          const newTotalCost =
            parseFloat(item.totalCost) +
            parseFloat(weight * selectedItem.sellingPrice);
          const newProfit =
            newWeight * selectedItem.sellingPrice -
            newWeight * selectedItem.buyingPrice;

          setTotalProfit(netProfit + newProfit - item.profit);
          setTotalSales(totalSales + newTotalCost - item.totalCost);

          return {
            ...item,
            weight: newWeight,
            totalCost: newTotalCost,
            profit: newProfit,
          };
        } else {
          return item;
        }
      });
      setCart(updatedCart);
    } else {
      const newCartItem = {
        name: selectedItem.name,
        weight: weight,
        profit:
          weight * selectedItem.sellingPrice -
          weight * selectedItem.buyingPrice,
        totalCost: weight * selectedItem.sellingPrice,
      };

      setTotalOrders(totalOrders + 1);
      setTotalProfit(netProfit + newCartItem.profit);
      setTotalSales(totalSales + newCartItem.totalCost);

      setCart([...cart, newCartItem]);
    }

    setSelectedItem({});
    setWeight("");
    setOrderCompleted(true);
  };

  const handleNextOrder = () => {
    setCart([]);
    setOrderCompleted(false);
  };

  const totalProfit = cart.reduce((acc, cur) => acc + cur.profit, 0);
  const totalCost = cart.reduce((acc, cur) => acc + cur.totalCost, 0);
  const numberOfOrders = cart.length;

  return (
    <div>
      <h1>Shop</h1>
      <div>
        <label>
          Item:
          <select
            value={selectedItem.name}
            onChange={(e) =>
              setSelectedItem(
                items.find((item) => item.name === e.target.value)
              )
            }
          >
            {items.map((item) => (
              <option key={item.name} value={item.name}>
                {item.name}
              </option>
            ))}
          </select>
        </label>
        <br />
        <label>
          Weight (kg):
          <input
            type="number"
            defaultValue={1}
            min={0}
            step={0.1}
            ref={(input) => (selectedItem.weightInput = input)}
          />
        </label>
        <button
          onClick={() => handleItemAddToCart(selectedItem.weightInput.value)}
        >
          Add to cart
        </button>
        {orderCompleted && (
          <button onClick={handleNextOrder}>Next Order</button>
        )}
      </div>
      <div>
        <h2>Cart</h2>
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Weight (kg)</th>
              <th>Total Cost</th>
              <th>Profit</th>
            </tr>
          </thead>
          <tbody>
            {cart.map((item, index) => (
              <tr key={index}>
                <td>{item.name}</td>
                <td>{item.weight}</td>
                <td>{item.totalCost.toFixed(2)}</td>
                <td>{item.profit.toFixed(2)}</td>
              </tr>
            ))}
          </tbody>
          <tfoot>
            <tr>
              <td colSpan="2">Total</td>
              <td>{totalCost.toFixed(2)}</td>
              <td>{totalProfit.toFixed(2)}</td>
            </tr>
          </tfoot>
        </table>
      </div>
      <div className="order-summary">
        <h2>Order Summary</h2>
        <p>Total Orders: {totalOrders}</p>
        <p>Total Profit: ${netProfit.toFixed(2)}</p>
        <p>Total Sales: ${totalSales.toFixed(2)}</p>
      </div>
    </div>
  );
};

export default App;
