import React, { useState } from "react";

const App = () => {
  const [selectedItem, setSelectedItem] = useState(null);
  const [buyingPrice, setBuyingPrice] = useState(0);
  const [sellingPrice, setSellingPrice] = useState(0);
  const [weight, setWeight] = useState(0);
  const [profit, setProfit] = useState(0);
  const [totalCost, setTotalCost] = useState(0);

  const handleItemSelect = (item, buyingPrice, sellingPrice) => {
    setSelectedItem(item);
    setBuyingPrice(buyingPrice);
    setSellingPrice(sellingPrice);
  };

  const handleWeightChange = (event) => {
    setWeight(event.target.value);
  };

  const handleProfitChange = (event) => {
    setProfit(event.target.value);
  };

  const handleCalculateCost = () => {
    const cost = weight * sellingPrice;
    setTotalCost(cost);
  };

  return (
    <div>
      <h1>Shop</h1>
      <div>
        <button onClick={() => handleItemSelect("apple", 100, 150)}>
          Apple
        </button>
        <button onClick={() => handleItemSelect("carrot", 50, 75)}>
          Carrot
        </button>
      </div>
      {selectedItem && (
        <div>
          <h2>{selectedItem}</h2>
          <p>Buying price per kg: {buyingPrice}</p>
          <p>Selling price per kg: {sellingPrice}</p>
          <label>
            Weight (kg):
            <input type="number" value={weight} onChange={handleWeightChange} />
          </label>
          <label>
            Profit:
            <input type="number" value={profit} onChange={handleProfitChange} />
          </label>
          <button onClick={handleCalculateCost}>Calculate cost</button>
          <p>Total cost: {totalCost}</p>
          <p>Profit: {profit}</p>
        </div>
      )}
    </div>
  );
};

export default App;
