import React, { useState } from "react";
import "./index.css";

const App = () => {
  const [cart, setCart] = useState([]);
  const [selectedItem, setSelectedItem] = useState({});
  const [weight, setWeight] = useState("");
  const [orderCompleted, setOrderCompleted] = useState(false);
  const [totalOrders, setTotalOrders] = useState(0);
  const [netProfit, setTotalProfit] = useState(0);
  const [totalSales, setTotalSales] = useState(0);
  const [newItemName, setNewItemName] = useState("");
  const [newItemBuyingPrice, setNewItemBuyingPrice] = useState("");
  const [newItemSellingPrice, setNewItemSellingPrice] = useState("");

  const handleAddToCart = (event) => {
    event.preventDefault();
    if (!selectedItem || !weight) {
    } else {
      const newCartItem = {
        item: selectedItem,
        weight: weight,
        profit:
          weight * selectedItem.sellingPrice -
          weight * selectedItem.buyingPrice,
        totalCost: weight * selectedItem.sellingPrice,
      };
      setCart([...cart, newCartItem]);
      setWeight("");
      setSelectedItem({});
      setTotalSales(totalSales + newCartItem.totalCost);
      setTotalProfit(netProfit + newCartItem.profit);
    }
  };

  const handleItemAddToCart = (weight) => {
    const existingCartItem = cart.find(
      (item) => item.name === selectedItem.name
    );

    if (existingCartItem) {
      const updatedCart = cart.map((item) => {
        if (item.name === selectedItem.name) {
          return {
            ...item,
            weight: parseFloat(item.weight) + parseFloat(weight),
            totalCost:
              parseFloat(item.totalCost) +
              parseFloat(weight * selectedItem.sellingPrice),
            profit:
              (parseFloat(item.weight) + parseFloat(weight)) *
                selectedItem.sellingPrice -
              (parseFloat(item.weight) + parseFloat(weight)) *
                selectedItem.buyingPrice,
          };
        } else {
          return item;
        }
      });
      setCart(updatedCart);
    } else {
      const newCartItem = {
        name: selectedItem.name,
        weight: weight,
        profit:
          weight * selectedItem.sellingPrice -
          weight * selectedItem.buyingPrice,
        totalCost: weight * selectedItem.sellingPrice,
      };
      setCart([...cart, newCartItem]);
    }
    setOrderCompleted(true);
  };

  const handleNewItemSubmit = (event) => {
    event.preventDefault();
    const newItem = {
      name: newItemName,
      buyingPrice: newItemBuyingPrice,
      sellingPrice: newItemSellingPrice,
    };
    setItems([...items, newItem]);
    setNewItemName("");
    setNewItemBuyingPrice("");
    setNewItemSellingPrice("");
  };

  const handleNextOrder = () => {
    setCart([]);
    setOrderCompleted(true);
    setTotalOrders(totalOrders + 1);
  };

  const numberOfOrders = cart.length;
  const totalProfit = cart.reduce((acc, cur) => acc + cur.profit, 0);
  const totalCost = cart.reduce((acc, cur) => acc + cur.totalCost, 0);

  return (
    <div>
      {!orderCompleted && (
        <div>
          <h2>Shop</h2>
          <div>
            <label>
              Item:
              <select
                value={selectedItem.name}
                onChange={(e) =>
                  setSelectedItem(
                    items.find((item) => item.name === e.target.value)
                  )
                }
              >
                {items.map((item) => (
                  <option key={item.name} value={item.name}>
                    {item.name}
                  </option>
                ))}
              </select>
            </label>
            <br />
            <label>
              Weight (kg):
              <input
                type="number"
                defaultValue={1}
                min={0}
                step={0.1}
                ref={(input) => (selectedItem.weightInput = input)}
              />
            </label>
            <button
              onClick={() =>
                handleItemAddToCart(selectedItem.weightInput.value)
              }
            >
              Add to cart
            </button>
            {orderCompleted && (
              <button onClick={handleNextOrder}>Next Order</button>
            )}
          </div>
          <div>
            <h2>Cart</h2>
            <table>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Weight (kg)</th>
                  <th>Total Cost</th>
                  <th>Profit</th>
                </tr>
              </thead>
              <tbody>
                {cart.map((item, index) => (
                  <tr key={index}>
                    <td>{item.name}</td>
                    <td>{item.weight}</td>
                    <td>{item.totalCost.toFixed(2)}</td>
                    <td>{item.profit.toFixed(2)}</td>
                  </tr>
                ))}
              </tbody>
              <tfoot>
                <tr>
                  <td colSpan="2">Total</td>
                  <td>{totalCost.toFixed(2)}</td>
                  <td>{totalProfit.toFixed(2)}</td>
                </tr>
              </tfoot>
            </table>
          </div>
          <form onSubmit={handleNewItemSubmit}>
            <label>
              Name:
              <input
                type="text"
                value={newItemName}
                onChange={(event) => setNewItemName(event.target.value)}
              />
            </label>
            <label>
              Buying Price:
              <input
                type="number"
                value={newItemBuyingPrice}
                onChange={(event) => setNewItemBuyingPrice(event.target.value)}
              />
            </label>
            <label>
              Selling Price:
              <input
                type="number"
                value={newItemSellingPrice}
                onChange={(event) => setNewItemSellingPrice(event.target.value)}
              />
            </label>
            <button type="submit">Add Item</button>
          </form>
        </div>
      )}

      {orderCompleted && (
        <div>
          <h2>Order Summary</h2>
          <p>Total Orders: {totalOrders}</p>
          <p>Total Profit: ${netProfit.toFixed(2)}</p>
          <p>Total Sales: ${totalSales.toFixed(2)}</p>
          <button onClick={handleNextOrder}>Next Order</button>
        </div>
      )}
    </div>
  );
};
export default App;
