import React, { useState } from "react";
import "./index.css";

const App = () => {
  const [before, after] = useState();
  const [fullname, setfullname] = useState();
  const [head, sethead] = useState("Find Your Age");

  const inputEvent = (event) => {
    after(event.target.value);
  };

  const onSubmit = () => {
    setfullname(before);
    sethead("Your Age Is");
  };
  return (
    <>
      <div>
        <h1>
          {head} {fullname}
        </h1>
        <input
          type="number"
          pattern="[0-9]*"
          max={12}
          placeholder="Enter Your Age"
          onChange={inputEvent}
          value={before}
        />
        <button onClick={onSubmit}>Find Age</button>
      </div>
    </>
  );
};

export default App;
