import React, { useState } from "react";
import "./index.css";

const App = () => {
  const red = "#000000";
  const [bg, setbg] = useState(red);
  const [btn, setbtn] = useState("Dont click please");

  const colour = () => {
    let newbg = "#ffd900";
    setbg(newbg);
    setbtn("How dare you ðŸ˜ ðŸ˜ ðŸ˜ ðŸ˜ ");
  };
  return (
    <>
      <div className="container" style={{ background: bg }}>
        <button onDoubleClick={colour}>{btn}</button>
      </div>
    </>
  );
};

export default App;
