import React, { useState } from "react";
import "./index.css";

const App = () => {
  let time = new Date().toLocaleTimeString();

  const [ctime, uTime] = useState(time);

  const Function = () => {
    time = new Date().toLocaleTimeString();
    uTime(time);
  };

  return (
    <>
      <div className="container">
        <h1 className="heading">{ctime}</h1>
        <button onClick={Function} className="btn">
          Get Time
        </button>
      </div>
    </>
  );
};

export default App;
